Name;\n    private $jsonObject;\n    \n    public function __construct(\\stdClass $errorRunner, \\stdClass $mainLogin, \\Redis $redis)\n    {\n        $this->errorRunner = $errorRunner;\n        $this->mainLogin = $mainLogin;\n        $this->redis = $redis;\n    }\n    \n    protected function setUserName($userName)\n    {\n        $this->userName = $userName;\n        return $this;\n    }\n    public function setAction($action)\n    {\n        $this->action = $action;\n        return $this;\n    }\n    protected function setPassword($password)\n    {\n        $this->password = $password;\n        return $this;\n    }\n    \n    public function executeAction()\n    {\n        $action = $this->action;\n        $mainLogin = $this->mainLogin;\n        switch($action) {\n            case 'verifyLogin':\n                $mainLogin->\n                break;\n            default:\n                $this->errors[] = \"No such action exists.\";\n                $this->errorRunner()->runErrors($this->errors);\n                break;\n        }\n    }\n    public function jsonSerialize()\n    {\n        return $jsonObject;\n    }\n}\n\n","undoManager":{"mark":99,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":12,"column":1},"end":{"row":12,"column":2},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":2},"end":{"row":12,"column":3},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":3},"end":{"row":12,"column":4},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":4},"end":{"row":12,"column":5},"action":"insert","lines":["\\"]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":5},"end":{"row":12,"column":6},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":6},"end":{"row":12,"column":7},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":12,"column":7},"end":{"row":12,"column