{"filter":false,"title":"Encryption.php","tooltip":"/security/Models/Encryption.php","undoManager":{"mark":92,"position":92,"stack":[[{"group":"doc","deltas":[{"start":{"row":0,"column":0},"end":{"row":66,"column":24},"action":"insert","lines":["<?php","namespace cellControl;","if (PHP_OS === \"WINNT\") {","    ini_set('error_reporting', E_ALL);","    ini_set(\"display_errors\", 1);","    ini_set('display_startup_errors', 1);","}","require_once(dirname(__DIR__) . DIRECTORY_SEPARATOR . 'init.php');","use \\cellControl\\Utilities;","// Encrypted cookie functions","// requires mcrypt: http://php.net/manual/en/book.mcrypt.php","class CookieEncrypt","{","    public function __construct()","    {","        $this->salt = self::getConstantEncrypt();","    }","    protected static function getConstantEncrypt($len = 15)","    {","        $salt = Utilities::generateRandomPassword($len);","        return $salt;","    }","    protected function encryptRawString($string)","    {","        $salt = $this->salt;","        // Configuration (must match decryption)","        $cipher_type = MCRYPT_RIJNDAEL_256;","        $cipher_mode = MCRYPT_MODE_CBC;","        // Using initialization vector adds more security","        $iv_size = mcrypt_get_iv_size($cipher_type, $cipher_mode);","        $iv =  mcrypt_create_iv($iv_size, MCRYPT_RAND);","        $encrypted_string = mcrypt_encrypt($cipher_type, $salt, $string, $cipher_mode, $iv);","        // Return initialization vector + encrypted string","        // We'll need the $iv when decoding.","        return $iv . $encrypted_string;","    }","    protected function decryptRawString($iv_with_string)","    {","        $salt = $this->salt;","        // Configuration (must match encryption)","        $cipher_type = MCRYPT_RIJNDAEL_256;","        $cipher_mode = MCRYPT_MODE_CBC;","        // Extract the initialization vector from the encrypted string.","        // The $iv comes before encrypted string and has fixed size.","        $iv_size = mcrypt_get_iv_size($cipher_type, $cipher_mode);","        $iv = substr($iv_with_string, 0, $iv_size);","        $encrypted_string = substr($iv_with_string, $iv_size);","        $string = mcrypt_decrypt($cipher_type, $salt, $encrypted_string, $cipher_mode, $iv);","        return $string;","    }","    // Encode after encryption to ensure encrypted characters are savable","    public function encString($string)","    {","        return base64_encode($this->encryptRawString($string));","    }","    // Decode before decryption","    public function decryptString($string)","    {","        return $this->decryptRawString(base64_decode($string));","    }","}","// Uncomment to show usage.","// $encryptor = new CookieEncrypt();","// $encryptedShell = $encryptor->encString(\"This is supposed to be encrypted.\");","// echo $encryptedShell;","// $decryptedShell = $encryptor->decryptString($encryptedShell);","// echo $decryptedShell;"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":10},"end":{"row":1,"column":21},"action":"remove","lines":["cellControl"]},{"start":{"row":1,"column":10},"end":{"row":1,"column":11},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":11},"end":{"row":1,"column":12},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":12},"end":{"row":1,"column":13},"action":"insert","lines":["c"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":13},"end":{"row":1,"column":14},"action":"insert","lines":["u"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":14},"end":{"row":1,"column":15},"action":"insert","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":15},"end":{"row":1,"column":16},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":16},"end":{"row":1,"column":17},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":1,"column":17},"end":{"row":1,"column":18},"action":"insert","lines":["y"]}